mapper.insert:
insert into creator (
                     cr_type
                     , user_code
                     , business_num
                     , address
                     , phone
                     , email
                     , introduce
                     , identi_num
                     , issue_type
                     , acc_user_nm
                     , ceo_nm
                     , creator_nm
 ) values (
            #{crType}
          , #{userCode}
          , #{businessNum}
          , #{address}
          , #{phone}
          , #{email}
          , #{introduce}
          , #{identiNum}
          , #{issueType}
          , #{accUserNm}
          , #{ceoNm}
          , #{creatorNm}
  )

mapper.update:
update creator set user_code = #{userCode}

                        , cr_type = #{crType}


                        , business_num = #{businessNum}


                        , address = #{address}


                        , phone = #{phone}


                        , email = #{email}


                        , introduce = #{introduce}


                        , identi_num = #{identiNum}


                        , ceo_nm = #{ceoNm}


                        , acc_user_nm = #{accUserNm}


                        , issue_type = #{issueType}


                        , creator_nm = #{creatorNm}

where user_code = #{userCode}

mapper.insert:
insert into creator_files (
            origin_name
            , update_name
            , register_date
            , delete_opt
            , user_code
            , file_type
) values (
        #{originName}
         , #{updateName}
         , NOW()
         , 1
         , #{userCode}
         , #{fileType}
         )

mapper.select:
select * from creator_files
where user_code = #{userCode}
and file_type = #{fileType}
